import { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import "./Read.scss";

const Read = () => {
  const [post, setPost] = useState();
  const { id } = useParams();

  // console.log(timestamp);

  useEffect(() => {
    //get and parse posts from local storage
    const makeAPICall = async () => {
      const response = await fetch(`http//localhost:3011/post/${id}`);
      const data = await response.json();
      setPost(data.post);
    };

    makeAPICall();
  }, [id]);

  const deletePost = async () => {
    if (window.confirm("Are you sure you want to delete?")) {
      await fetch(`${APIURL}/post/${id}`, { method: "DELETE" });
    }
  };

  if (!post) {
    return <h1>Hold your üê¥</h1>;
  }

  return (
    <div id="read">
      <h2>{post.title}</h2>
      <h5>{post.author}</h5>
      <h6>{new Date(post.timestamp).toLocaleString()}</h6>
      <hr />
      <p>{post.content}</p>
      <Link to="/">Return to Home</Link>
      <button className="btn btn-danger" onClick={deletePost}>
        Delete Post
      </button>
    </div>
  );
};

export default Read;
