import { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import "./Read.scss";
import APIURL from "./APIURL";

const Read = () => {
  const [post, setPost] = useState();
  const { id } = useParams();
  const navigateTo = useNavigate();

  // console.log(timestamp);

  useEffect(() => {
    //get and parse posts from local storage
    const makeAPICall = async () => {
      const response = await fetch(`${APIURL}/post/${id}`);
      const data = await response.json();
      setPost(data.post);
    };

    makeAPICall();
  }, [id]);

  const deletePost = async () => {
    if (window.confirm("Are you sure you want to delete?")) {
      await fetch(`${APIURL}/post/${id}`, { method: "DELETE" });
    }
  };

  const postComment = async (event) => {
    event.preventDefault();

    const response = await fetch(`${APIURL}/comment`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({}),
    });
  };

  if (!post) {
    return <h1>Hold your üê¥</h1>;
  }

  return (
    <div id="read">
      <h2>{post.title}</h2>
      <h5>{post.author}</h5>
      <h6>{new Date(post.timestamp).toLocaleString()}</h6>
      <hr />
      <p>{post.content}</p>
      <Link to="/">Return to Home</Link>
      <button className="btn btn-danger" onClick={deletePost}>
        Delete Post
      </button>

      <hr />
      <form>
        <label>Comment text: </label>
        <textarea id="content" className="form-control"></textarea>

        <label>Name:</label>
        <input type="text" id="author" className="form-control" />

        <button type="submit" className="btn btn-success">
          Post
        </button>
      </form>
    </div>
  );
};

export default Read;
